package main.java;/**  * A superclass of various sorting algorithms for sorting an array * of items with a factory that chooses based on an int key. */public abstract class SortAlgos {	public abstract String getName();	public abstract void sort(Item[] vec);		public static SortAlgos sortFactory(int choice) {		switch (choice) {        case 0 : {        	return new BubbleSort();        	}        case 1 : {        	return new SelectionSort();        	}        case 2 : {        	return new InsertionSort();        	}        case 3 : {        	return new MergeSort();        	}        case 4 : {        	return new QuickSort();        	}        case 5 : {        	return new HeapSort();        	}        default:        	return new BubbleSort();         }	}}